    /**
     * Erstellt aus dem im Konstruktor erzeugten temp das wirkliche Level indem es alle Blöcke aus temp einer Liste von Blöcken hinzufügt.
     * @param temp Zweidimensionales Integerarray. Ints repräsentieren die Blockart.
     */
    private void initBlocks(int[][] temp) {
        for (int i = 0; i < temp.length; i++) {
            for (int j = 0; j < temp[i].length; j++) {
                levelEntities.add(new Block(j * Block.BLOCK_SIZE, i * Block.BLOCK_SIZE, BlockType.createFromID(temp[i][j])));

                if (i == temp.length - 1 && j == temp[i].length - 1)
                    lastBlock = (Block)levelEntities.get(levelEntities.size() - 1);

                //Dieser Teil münzt noch darauf, dass wenn es mal mehr Level geben sollte deren Ende stets gleich aussieht!
                if (i == 5 && j == 198)
                    finishBlock = (Block)levelEntities.get(levelEntities.size() - 1);
            }
        }
    }

